import getMathHandler from './applyMath.js'
const defaultState = {
    last: '',
    now: '',
    lastSymbol: true
}

const reducer = (state = defaultState, action) => {
    let newString = state.now;
    let lastString = state.last;
    switch (action.type) {
        case 'NEW_ACTION':
            if(newString.lenght === 0 || state.lastSymbol) {
                newString = state.now;
            } else {
                newString = state.now + ' ' + action.payload + ' ';
            }
            return {
                last: null,
                now: newString,
                lastSymbol: true
            };
        case 'NEW_NUMBER':
            newString =  state.now + action.payload;
            return {
                last:null,
                now: newString,
                lastSymbol: false
            }
        case 'DELETE_ONE':
            newString = state.now.slice(0, -1)
            return {
                last:null,
                now: newString,
                lastSymbol: false
            }
        case 'DELETE_ALL':
            newString = '';
            return {
                last:null,
                now: newString,
                lastSymbol: true
            }
        case 'TOTAL':
            lastString = newString;
            newString = getMathHandler(newString);
            return {
                last:lastString,
                now: '',
                lastSymbol: false
            }
        default:
            return state;
    }
}

export default reducer;
