const defaultState = {
    last: null,
    now: null
}

const reducer = (state = defaultState, action) => {
    console.log(state, action);
    switch (action.type) {
        case 'NEW_ACTION':
            const newString = now + ' ' + action.payload;
            return {
                last: null,
                now: newString
            };
        case 'NEW_NUMBER':
            return {
                last:null,
                now: action.payload
            }
        default:
            return state;
    }
}

export default reducer;
