const defaultState = {
    last: null,
    now: null,
    lastSymbol: null
}

const reducer = (state = defaultState, action) => {
    let newString = state.now;
    switch (action.type) {
        case 'NEW_ACTION':
            newString = state.now + ' ' + action.payload + ' ';
            return {
                last: null,
                now: newString
            };
        case 'NEW_NUMBER':
            newString = state.now + action.payload;
            return {
                last:null,
                now: newString
            }
        default:
            return state;
    }
}

export default reducer;
