const defaultState = {
    last: null,
    now: '',
    lastSymbol: null
}

const reducer = (state = defaultState, action) => {
    let newString = state.now;
    console.log(state);
    switch (action.type) {
        case 'NEW_ACTION':
            if(newString.lenght === 0) {
                newString = '';
            }
            newString = state.now + ' ' + action.payload + ' ';
            return {
                last: null,
                now: newString,
                lastSymbol: action.payload
            };
        case 'NEW_NUMBER':
            newString =  state.now + action.payload;
            return {
                last:null,
                now: newString,
                lastSymbol: null
            }
        default:
            return state;
    }
}

export default reducer;
