const defaultState = {
    last: null,
    now: null,
    lastSymbol: null
}

const reducer = (state = defaultState, action) => {
    let newString = state.now;
    switch (action.type) {
        case 'NEW_ACTION':
            newString = state.now + ' ' + action.payload + ' ';
            return {
                last: null,
                now: newString,
                lastSymbol: action.payload
            };
        case 'NEW_NUMBER':
            newString = (state.now === null) ? null : state.now + String(action.payload);
            return {
                last:null,
                now: newString,
                lastSymbol: null
            }
        default:
            return state;
    }
}

export default reducer;
