const defaultState = {
    last: null,
    now: '',
    lastSymbol: true
}

const reducer = (state = defaultState, action) => {
    let newString = state.now;
    switch (action.type) {
        case 'NEW_ACTION':
            if(newString.lenght === 0 || state.lastSymbol) {
                newString = state.now;
            } else {
                newString = state.now + ' ' + action.payload + ' ';
            }
            return {
                last: null,
                now: newString,
                lastSymbol: true
            };
        case 'NEW_NUMBER':
            newString =  state.now + action.payload;
            return {
                last:null,
                now: newString,
                lastSymbol: false
            }
        case 'DELETE_ONE':

            return {
                last:null,
                now: newString,
                lastSymbol: false
            }
        default:
            return state;
    }
}

export default reducer;
